import "./ExpenseForm.css";
import { useState } from "react";


const ExpenseForm = () => {


    const [enterdTitle, setEnterdTitle] = useState('');
    const [enterdAmount, setEnterdAmount] = useState('');
    const [enterdDate, setEnterdDate] = useState('');

    const titleChangeHandler = (event) => {
        setEnterdTitle(event.target.value);
    };
    
    const amountChangeHandler = (event) => {
        setEnterdAmount(event.target.value);
    };
    
    const dateChangeHandler = (event) => {
        setEnterdDate(event.target.value);
        console.log(dateChangeHandler);
    };

    const submitHnadler = (event) => {
        event.preventDefault();

        const expenseDate = {
            title: enterdTitle,
            amount: enterdAmount,
            date: new Date(enterdDate)
        }
        setEnterTitle()
    }

    return (
    <form onSubmit={submitHnadler}>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <lable>Title</lable>
          <input type="text" value = {enterdTitle} onChange={titleChangeHandler} />
        </div>
        <div className="new-expense__control">
          <lable>Amount</lable>
          <input type="number" value = {enterdAmount} min="0.01" step="0.01" onChange={amountChangeHandler} />
        </div>
        <div className="new-expense__control">
          <lable>Date</lable>
          <input type="date" value = {enterdDate} min="2019-01-01" max="2022-12-31" oneChange={dateChangeHandler} />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
